mapper.insert:
insert into erp_auth
userid, orgId, auth
#{userid}, #{orgId}, #{auth}

mapper.delete:
delete from erp_auth


        userid = #{userid}


        and orgId = #{orgId}

mapper.select:
select * from (
select rownum rn, erp_auth.* from (
select userid, auth, orgId
from erp_auth


    ${column} like #{word}

order by userid desc
) erp_auth order by rn desc
)
where rn between #{startRow} and #{endRow}

SELECT userid, passwd, name, phone, address, email, gender, birthday, joindate, enabled, org_id
FROM erp_user
WHERE 1=1


        AND userid = #{value}


        AND org_id = #{value}

